import React, { useEffect, useState } from "react";
import { usePlayer } from "../../contexts/PlayerContext";
import api from "../../utils/api";

const SongPlayer = () => {
  const { currentSong, clearSong } = usePlayer();
  const [audio, setAudio] = useState(null); // Audio element
  const [isPlaying, setIsPlaying] = useState(false);
  const [duration, setDuration] = useState(0); // Song duration (seconds)
  const [progress, setProgress] = useState(0); // Playback progress (seconds)
  const [error, setError] = useState("");

  useEffect(() => {
    if (currentSong) {
      fetchSongStream();
    }

    // Cleanup audio on unmount or when currentSong changes
    return () => {
      if (audio) {
        audio.pause();
        audio.removeEventListener("timeupdate", handleTimeUpdate);
        audio.removeEventListener("ended", handleSongEnd);
      }
    };
  }, [currentSong]);

  const fetchSongStream = async () => {
    if (!currentSong) return;
    try {
      const response = await api.get(`/stream/play/${currentSong.id}`, {
        responseType: "blob",
      });
      const url = URL.createObjectURL(response.data);
      const audioElement = new Audio(url);

      // Add event listeners
      audioElement.addEventListener("timeupdate", handleTimeUpdate);
      audioElement.addEventListener("ended", handleSongEnd);

      setAudio(audioElement);
      setDuration(audioElement.duration || 0);
      setProgress(0);
      setIsPlaying(false);
      setError("");
    } catch {
      setError("Failed to stream the song. Please try again.");
      clearSong();
    }
  };

  const handleTimeUpdate = () => {
    if (audio) {
      setProgress(audio.currentTime);
      setDuration(audio.duration);
    }
  };

  const handleSongEnd = () => {
    clearSong();
    setIsPlaying(false);
    setProgress(0);
  };

  // Play the song
  const handlePlay = () => {
    if (audio) {
      audio.play();
      setIsPlaying(true);
    }
  };

  // Pause the song
  const handlePause = () => {
    if (audio) {
      audio.pause();
      setIsPlaying(false);
    }
  };

  // Stop the song
  const handleStop = () => {
    if (audio) {
      audio.pause();
      audio.currentTime = 0;
      setIsPlaying(false);
      setProgress(0);
    }
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
  };

  if (!currentSong) {
    return (
      <div className="bg-music-background text-white p-4">No song selected</div>
    );
  }

  return (
    <div className="bg-music-background text-white p-6 rounded shadow-md">
      <h2 className="text-2xl font-bold mb-4">{currentSong.title}</h2>
      {error ? (
        <div className="bg-red-500 p-3 rounded">{error}</div>
      ) : (
        <>
          <div className="flex items-center gap-4 mb-4">
            <button
              onClick={handlePlay}
              disabled={isPlaying}
              className={`py-2 px-4 rounded ${
                isPlaying
                  ? "bg-gray-600"
                  : "bg-music-primary hover:bg-green-600"
              }`}
            >
              Play
            </button>
            <button
              onClick={handlePause}
              disabled={!isPlaying}
              className={`py-2 px-4 rounded ${
                isPlaying
                  ? "bg-music-primary hover:bg-green-600"
                  : "bg-gray-600"
              }`}
            >
              Pause
            </button>
            <button
              onClick={handleStop}
              className="py-2 px-4 rounded bg-red-600 hover:bg-red-700"
            >
              Stop
            </button>
          </div>
          <div className="w-full bg-gray-800 h-2 rounded overflow-hidden mb-4">
            <div
              className="bg-music-primary h-2"
              style={{ width: `${(progress / duration) * 100 || 0}%` }}
            ></div>
          </div>
          <div className="w-full flex justify-between text-sm text-music-secondary mb-4">
            <span>{formatTime(progress)}</span>
            <span>{formatTime(duration)}</span>
          </div>
        </>
      )}
    </div>
  );
};

export default SongPlayer;
