import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../utils/api";

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    bio: "",
    profilePicture: "",
  });

  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await api.post("/auth/signup", formData);

      setSuccessMessage("Registration successful! Please login."); // response.data.message
      setErrorMessage("");

      setFormData({
        username: "",
        email: "",
        password: "",
        bio: "",
        profilePicture: "",
      });

      useEffect(() => {
        setTimeout(() => {
          navigate("/");
        }, 3000); // Redirect to login page after 3 seconds
      }, []);
    } catch (error) {
      setErrorMessage("Registration failed. Please try again."); // error.response.data.message
      setSuccessMessage("");
    }
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-music-background text-white">
      <form action={handleRegister}>
        <h2 className="text-2xl font-bold mb-6 text-music-primary">
          Create an Account
        </h2>
        {errorMessage && (
          <p className="mb-4 text-red-500 text-sm">{errorMessage}</p>
        )}
        {successMessage && (
          <p className="mb-4 text-green-500 text-sm">{successMessage}</p>
        )}
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          className="w-full mb-4 p-2 bg-gray-900 text-white rounded"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          className="w-full mb-4 p-2 bg-gray-900 text-white rounded"
          required
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          className="w-full mb-4 p-2 bg-gray-900 text-white rounded"
        />
        <textarea
          name="bio"
          placeholder="Short Bio"
          value={formData.bio}
          onChange={handleChange}
          className="w-full mb-4 p-2 bg-gray-900 text-white rounded"
        />
        <input
          type="url"
          name="profilePicture"
          placeholder="Profile Picture URL"
          value={formData.profilePicture}
          onChange={handleChange}
          className="w-full mb-4 p-2 bg-gray-900 text-white rounded"
        />
        <button
          type="submit"
          className="w-full bg-music-primary p-2 rounded hover:bg-green-700"
        >
          Register
        </button>
      </form>
    </div>
  );
};

export default Register;
